INSERT INTO `wp_posts` VALUES("470", "1", "2014-12-14 16:40:48", "2014-12-14 16:40:48", "<h3 class=\"text-center\" style=\"text-align: center;\">Web &amp; Graphic designer basé à Montréal, avec dix ans d\'expérience. Mon principal objectif est d\'atteindre les buts de communication de mes clients avec des projets de conception attrayante et fonctionnelle.</h3>\r\n<p style=\"text-align: center;\">J\'ai été impliqué dans de nombreux aspects de la conception, de production et la conception des projets attrayants et fonctionnels, y compris la conception de sites web, conception de médias interactifs, conception de logo, conception d\'impression, conception éditoriale, et des campagnes publicitaires.</p>", "À propos", "", "inherit", "closed", "open", "", "275-revision-v1", "", "", "2014-12-14 16:40:48", "2014-12-14 16:40:48", "", "275", "http://maubayuelo.com/275-revision-v1/", "0", "revision", "", "0");
